import { Component } from '@angular/core';
import { Observable } from 'rxjs';
import { ProductService } from '../product.service';

@Component({
  selector: 'app-search',  // Il selettore per il componente Search
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {
  searchTerm: string;
  searchResults$: Observable<any>;

  constructor(private productService: ProductService) {}

  onSearch(inputElement: HTMLInputElement): void {
    const query = inputElement.value.trim();
    if (!query) {
      return;
    }
    this.searchTerm = query;
    this.searchResults$ = this.productService.searchProducts(this.searchTerm);
    this.searchResults$.subscribe(results => console.log(results));
  }
}
<h2>Search Products</h2>

<div>
  <input type="text" #searchTerm placeholder="Enter product name">
  <button (click)="onSearch(searchTerm)">Find</button>
</div>

<div *ngIf="searchResults">
  <div *ngIf="!searchResults.products || searchResults.products.length == 0">
    No products found for '{{ searchQuery }}'
  </div>

  <div *ngIf="searchResults.products && searchResults.products.length > 0">
    <h2>Search Results</h2>
    <div class="row">
      <div class="col-sm-6 col-md-4" *ngFor="let product of searchResults.products">
        <div class="product-thumbnail">
          <img src="{{ product.image_front_small_url }}" class="img-fluid">
          <div class="product-caption">
            <h4>
              <a [routerLink]="['/products', product.id]">
                {{ product.product_name }}
              </a>
            </h4>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
